    const isMenuOpen = ref<boolean>(false);
    const menu = ref<HTMLElement | null>(null);

    const updateMenuStatus = (status: boolean) => {
        isMenuOpen.value = status;
    };

    onClickOutside(menu, () => updateMenuStatus(false));
